const bel = require('bel')
const csjs = require('csjs-inject')

function template() {
    const css = style
    const btn = bel`<button class=${css.click} onclick=${() => clickHandler() }>Click</button>`
    const el = bel`
    <section class=${css.container}>
        <h1>Hello world</h1>
        <p>My first app</p>
        ${btn}
    </section>`

    function clickHandler() {
        console.log('you clicked');
    }

    return el
}

module.exports = template

const style = csjs`
.container {
    
}
.click {
    background-color: #000;
    color: #fff;
}
.click:hover {
    background-color: #d9d9d9;
    color: #000;
}
`